'use client';

import { useState } from 'react';
import { Trash2, X } from 'lucide-react';
import axiosInstance from '../lib/axios';
import { useBookings } from '../hooks/useBookings';
import { useBulkUndo } from '../hooks/useBulkUndo';
import { toast } from 'react-toastify';
import { logger } from '@entrip/shared';

interface BulkActionBarProps {
  selectedIds: string[];
  onClearSelection: () => void;
}

export default function BulkActionBar({ selectedIds, onClearSelection }: BulkActionBarProps) {
  const { bookings, mutate } = useBookings();
  const [isDeleting, setIsDeleting] = useState(false);
  const { addUndoItem } = useBulkUndo();

  if (selectedIds.length === 0) return null;

  const handleBulkDelete = async () => {
    setIsDeleting(true);

    try {
      // ì‚­ì œë  ì˜ˆì•½ ë°ì´í„°ë¥¼ ì €ì¥
      const deletedBookings = bookings.filter(booking => selectedIds.includes(booking.id));

      // Optimistic update - ì„ íƒëœ í•­ëª©ë“¤ì„ ì¦‰ì‹œ ì œê±°
      await mutate(
        async (currentData: any) => {
          return currentData?.filter((booking: any) => 
            !selectedIds.includes(booking.id)
          );
        },
        { revalidate: false }
      );

      // API í˜¸ì¶œ
      const response = await axiosInstance.delete('/api/bookings/bulk', {
        data: { ids: selectedIds }
      });

      logger.info('[BulkActionBar]', `Bulk delete successful - DELETE 200, deleted: ${response.data.deleted}`);

      // Undo ìŠ¤íƒì— ì¶”ê°€
      addUndoItem('delete', deletedBookings);

      // ì„±ê³µ í›„ ì„œë²„ ë°ì´í„°ë¡œ ê°±ì‹ 
      await mutate();

      // ì„ íƒ ì´ˆê¸°í™”
      onClearSelection();
    } catch (error) {
      logger.error('[BulkActionBar]', 'Bulk delete failed:', error);
      toast.error('ì‚­ì œì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤');
      // ì‹¤íŒ¨ ì‹œ ë¡¤ë°±
      await mutate();
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="fixed bottom-6 left-1/2 transform -translate-x-1/2 bg-gray-900 text-white rounded-lg shadow-lg px-4 py-3 flex items-center gap-4 z-50">
      <span className="text-sm">
        {selectedIds.length}ê°œ ì„ íƒë¨
      </span>

      <button
        onClick={handleBulkDelete}
        disabled={isDeleting}
        className="flex items-center gap-2 px-3 py-1.5 bg-red-600 hover:bg-red-700 rounded text-sm disabled:opacity-50"
      >
        <Trash2 className="w-4 h-4" />
        {isDeleting ? 'ì‚­ì œ ì¤‘...' : 'ì„ íƒ ì‚­ì œ'}
      </button>

      <button
        onClick={onClearSelection}
        className="p-1.5 hover:bg-gray-800 rounded"
      >
        <X className="w-4 h-4" />
      </button>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { ReactProfiler, onRenderCallback, startProfiling, stopProfiling } from '../utils/profiler';
import { memoryProfiler } from '../utils/memory-profiler';
import CalendarVirtual from '../features/calendar/CalendarVirtual';
import { logger } from '@entrip/shared';

interface CalendarProfilerProps {
  currentDate: Date;
  bookings: any[];
  onDayClick?: (date: Date) => void;
  onBookingClick?: (booking: any) => void;
}

export default function CalendarProfiler({ currentDate, bookings, onDayClick, onBookingClick }: CalendarProfilerProps) {
  const [isProfiling, setIsProfiling] = useState(false);
  const [profileResults, setProfileResults] = useState<any>(null);
  const [memoryResults, setMemoryResults] = useState<any>(null);

  const handleStartProfiling = () => {
    logger.info('[CalendarProfiler]', 'Starting performance and memory profiling');
    setIsProfiling(true);
    startProfiling();
    memoryProfiler.start();
  };

  const handleStopProfiling = () => {
    logger.info('[CalendarProfiler]', 'Stopping profiling');
    setIsProfiling(false);

    // Stop performance profiling
    const perfResults = stopProfiling();
    setProfileResults(perfResults);

    // Stop memory profiling
    const memResults = memoryProfiler.stop();
    setMemoryResults(memResults);
  };

  // Clean up effect
  useEffect(() => {
    return () => {
      if (isProfiling) {
        memoryProfiler.stop();
      }
    };
  }, [isProfiling]);

  return (
    <div>
      {/* Profiling controls */}
      <div className="mb-4 p-4 bg-gray-100 rounded-lg">
        <div className="flex items-center gap-4">
          <button
            onClick={handleStartProfiling}
            disabled={isProfiling}
            className="px-4 py-2 bg-blue-500 text-white rounded disabled:bg-gray-300"
          >
            Start Profiling
          </button>
          <button
            onClick={handleStopProfiling}
            disabled={!isProfiling}
            className="px-4 py-2 bg-red-500 text-white rounded disabled:bg-gray-300"
          >
            Stop Profiling
          </button>
          {isProfiling && (
            <span className="text-sm text-gray-600">
              ğŸ”´ Profiling in progress...
            </span>
          )}
        </div>

        {/* Results display */}
        {profileResults && (
          <div className="mt-4 p-3 bg-white rounded border">
            <h4 className="font-medium mb-2">Performance Results:</h4>
            <div className="text-sm space-y-1">
              <div>Total renders: {profileResults.length}</div>
              <div>Slow renders (&gt;16.67ms): {profileResults.filter((r: any) => r.actualDuration > 16.67).length}</div>
              <div>FPS estimate: {(1000 / (profileResults.reduce((sum: number, r: any) => sum + r.actualDuration, 0) / profileResults.length)).toFixed(1)}</div>
            </div>
          </div>
        )}

        {memoryResults && (
          <div className="mt-4 p-3 bg-white rounded border">
            <h4 className="font-medium mb-2">Memory Results:</h4>
            <div className="text-sm space-y-1">
              <div>Initial: {(memoryResults.initialMemory / 1024 / 1024).toFixed(2)}MB</div>
              <div>Final: {(memoryResults.finalMemory / 1024 / 1024).toFixed(2)}MB</div>
              <div>Change: {memoryResults.percentageChange > 0 ? '+' : ''}{memoryResults.percentageChange.toFixed(1)}%</div>
              <div className={memoryResults.percentageChange < -30 ? 'text-green-600 font-medium' : ''}>
                {memoryResults.percentageChange < -30 && 'âœ… Memory reduction target achieved!'}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Calendar wrapped in Profiler */}
      <ReactProfiler id="CalendarVirtual" onRender={onRenderCallback}>
        <CalendarVirtual
          currentDate={currentDate}
          bookings={bookings}
          onDayClick={onDayClick}
          onBookingClick={onBookingClick}
        />
      </ReactProfiler>
    </div>
  );
}

