const CalendarVirtualMemo = memo(function CalendarVirtual({ currentDate, bookings, onDayClick, onBookingClick }: CalendarVirtualProps) {
  // Window width state for responsive grid
  const [windowWidth, setWindowWidth] = useState(typeof window !== 'undefined' ? window.innerWidth : 800);

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // ì›”ì˜ ëª¨ë“  ë‚ ì§œ ê³„ì‚° - ë©”ëª¨ì´ì œì´ì…˜
  const { allDays } = useMemo(() => {
    const monthStart = startOfMonth(currentDate);
    const monthEnd = endOfMonth(currentDate);
    const days = eachDayOfInterval({ start: monthStart, end: monthEnd });

    // ì²« ë²ˆì§¸ ë‚ ì˜ ìš”ì¼ (0=ì¼ìš”ì¼)
    const startDayOfWeek = monthStart.getDay();

    // ì´ì „ ë‹¬ì˜ ë‚ ì§œë“¤ ì¶”ê°€
    const prevMonthDays = Array.from({ length: startDayOfWeek }, (_, i) => {
      const date = new Date(monthStart);
      date.setDate(date.getDate() - (startDayOfWeek - i));
      return date;
    });

    // ë‹¤ìŒ ë‹¬ì˜ ë‚ ì§œë“¤ ì¶”ê°€ (ì´ 42ì¼ = 6ì£¼)
    const totalDays = [...prevMonthDays, ...days];
    const remainingDays = 42 - totalDays.length;
    const nextMonthDays = Array.from({ length: remainingDays }, (_, i) => {
      const date = new Date(monthEnd);
      date.setDate(date.getDate() + i + 1);
      return date;
    });

    const allDaysArray = [...totalDays, ...nextMonthDays];

    // ì£¼ë³„ë¡œ ê·¸ë£¹í™”
    const weeksArray = [];
    for (let i = 0; i < allDaysArray.length; i += 7) {
      weeksArray.push(allDaysArray.slice(i, i + 7));
    }

    return { allDays: allDaysArray };
  }, [currentDate]);

  // ë‚ ì§œë³„ë¡œ ì˜ˆì•½ ê·¸ë£¹í™” - ìµœì í™”ëœ ë©”ëª¨ì´ì œì´ì…˜
  const bookingsByDate = useMemo(() => {
    const map = new Map<string, any[]>();
    bookings.forEach((booking) => {
      const date = booking.departureDate;
      if (!map.has(date)) {
        map.set(date, []);
      }
      map.get(date)!.push(booking);
    });
    return map;
  }, [bookings]);

  // ì½œë°± ë©”ëª¨ì´ì œì´ì…˜
  const handleDayClick = useCallback(onDayClick || (() => {}), [onDayClick]);
  const handleBookingClick = useCallback(onBookingClick || (() => {}), [onBookingClick]);

  return (
    <div className="bg-white rounded-lg border border-gray-200" data-testid="calendar-virtual">
      {/* ìš”ì¼ í—¤ë” */}
      <div className="flex border-b border-gray-200 bg-gray-50">
        {['ì¼', 'ì›”', 'í™”', 'ìˆ˜', 'ëª©', 'ê¸ˆ', 'í† '].map((day, index) => (
          <div
            key={day}
            className={clsx(
              'flex-1 py-3 text-center text-sm font-medium',
              index === 0 && 'text-red-500',
              index === 6 && 'text-blue-500'
            )}
          >
            {day}
          </div>
        ))}
      </div>

      {/* Grid ê°€ìƒ ìŠ¤í¬ë¡¤ ì ìš©ëœ ìº˜ë¦°ë” */}
      <Grid
        className="calendar-grid"
        data-testid="scroll-container"
        height={600} // ìº˜ë¦°ë” ë†’ì´
        width={windowWidth > 768 ? 800 : windowWidth - 32} // ë°˜ì‘í˜• ë„ˆë¹„
        rowCount={6} // 6ì£¼
        columnCount={7} // 7ì¼
        rowHeight={100} // ê° í–‰ì˜ ë†’ì´
        columnWidth={windowWidth > 768 ? 114 : Math.floor((windowWidth - 32) / 7)} // ê° ì—´ì˜ ë„ˆë¹„
        itemData={{
          allDays,
          bookingsByDate,
          onDayClick: handleDayClick,
          onBookingClick: handleBookingClick,
          currentMonth: currentDate,
          daysPerWeek: 7
        }}
      >
        {Cell}
      </Grid>
    </div>
  );
});

CalendarVirtualMemo.displayName = 'CalendarVirtual';

export default CalendarVirtualMemo;

