// ì„ì‹œ ë”ë¯¸ ë°ì´í„° ìƒì„± í•¨ìˆ˜
const generateDummyBookings = (month: Date): Record<string, BookingEvent[]> => {
  const bookings: Record<string, BookingEvent[]> = {};
  const year = month.getFullYear();
  const monthNum = month.getMonth();

  // ì—¬í–‰ì§€ ëª©ë¡
  const destinations = [
    'ì œì£¼ë„', 'ë¶€ì‚°', 'ê²½ì£¼', 'ê°•ë¦‰', 'ì „ì£¼', 'ì—¬ìˆ˜', 'í†µì˜', 'ê±°ì œë„',
    'ì¼ë³¸ ì˜¤ì‚¬ì¹´', 'ì¼ë³¸ ë„ì¿„', 'ì¼ë³¸ í›„ì¿ ì˜¤ì¹´', 'ì¼ë³¸ êµí† ',
    'ë² íŠ¸ë‚¨ ë‹¤ë‚­', 'ë² íŠ¸ë‚¨ í˜¸ì¹˜ë¯¼', 'íƒœêµ­ ë°©ì½•', 'íƒœêµ­ ì¹˜ì•™ë§ˆì´',
    'ì‹±ê°€í¬ë¥´', 'í™ì½©', 'ëŒ€ë§Œ íƒ€ì´ë² ì´', 'í•„ë¦¬í•€ ì„¸ë¶€'
  ];

  // íŒ€ ìœ í˜•
  const teamTypes = [
    'ê°€ì¡±ì—¬í–‰', 'ì‹ í˜¼ì—¬í–‰', 'íš¨ë„ê´€ê´‘', 'ì¹œêµ¬ì—¬í–‰', 'ë‹¨ì²´ì—¬í–‰', 
    'ìˆ˜í•™ì—¬í–‰', 'ì›Œí¬ìƒµ', 'ë™í˜¸íšŒ', 'VIPíˆ¬ì–´', 'íŒ¨í‚¤ì§€íˆ¬ì–´'
  ];

  // ë§¤ë‹ˆì € ì´ë¦„
  const managers = ['ê¹€ë¯¼ìˆ˜', 'ì´ì§€ì˜', 'ë°•ì¤€í˜', 'ìµœì„œì—°', 'ì •íƒœí˜¸'];

  // ì›”ë³„ ì‹œì¦Œì— ë”°ë¥¸ ì˜ˆì•½ ìˆ˜ ì¡°ì •
  const seasonMultiplier = [0.7, 0.8, 1.0, 1.2, 1.5, 1.3, 1.6, 1.8, 1.4, 1.1, 0.9, 0.8][monthNum];
  const bookingCount = Math.floor((Math.random() * 15 + 20) * seasonMultiplier);

  for (let i = 0; i < bookingCount; i++) {
    const day = Math.floor(Math.random() * 28) + 1;
    const dateKey = format(new Date(year, monthNum, day), 'yyyy-MM-dd');

    if (!bookings[dateKey]) {
      bookings[dateKey] = [];
    }

    const destination = destinations[Math.floor(Math.random() * destinations.length)];
    const teamType = teamTypes[Math.floor(Math.random() * teamTypes.length)];
    const manager = managers[Math.floor(Math.random() * managers.length)];

    // ì—¬í–‰ ìœ í˜•ë³„ ì¸ì›ìˆ˜ ì¡°ì •
    let paxCount = 2;
    if (teamType.includes('ê°€ì¡±')) paxCount = Math.floor(Math.random() * 4) + 3;
    else if (teamType.includes('ë‹¨ì²´') || teamType.includes('ìˆ˜í•™')) paxCount = Math.floor(Math.random() * 30) + 20;
    else if (teamType.includes('ë™í˜¸íšŒ') || teamType.includes('ì›Œí¬ìƒµ')) paxCount = Math.floor(Math.random() * 15) + 10;
    else if (teamType.includes('ì‹ í˜¼')) paxCount = 2;
    else paxCount = Math.floor(Math.random() * 8) + 2;

    // ëª©ì ì§€ë³„ ê°€ê²© ì¡°ì •
    const isOverseas = destination.includes('ì¼ë³¸') || destination.includes('ë² íŠ¸ë‚¨') || 
                       destination.includes('íƒœêµ­') || destination.includes('ì‹±ê°€í¬ë¥´') || 
                       destination.includes('í™ì½©') || destination.includes('ëŒ€ë§Œ') || destination.includes('í•„ë¦¬í•€');

    const basePrice = isOverseas ? 800000 : 300000;
    const pricePerPerson = basePrice + Math.floor(Math.random() * 200000);
    const revenue = pricePerPerson * paxCount;
    const cost = Math.floor(revenue * (0.7 + Math.random() * 0.15)); // 70-85% ì›ê°€ìœ¨

    const types: BookingEvent['typeCode'][] = ['GF', 'IN', 'HM', 'AT'];
    const statuses: BookingEvent['status'][] = [BookingStatus.PENDING, BookingStatus.CONFIRMED, BookingStatus.CANCELLED];
    const status = statuses[Math.random() < 0.7 ? 1 : Math.random() < 0.9 ? 0 : 2]; // 70% í™•ì •, 20% ëŒ€ê¸°, 10% ì·¨ì†Œ

    bookings[dateKey].push({
      id: `booking-${year}-${monthNum}-${i}`,
      typeCode: types[Math.floor(Math.random() * types.length)],
      name: `${destination} ${teamType}`,
      status: status,
      manager: manager,
      paxCount: paxCount,
      date: dateKey,
      revenue: revenue,
      cost: cost
    });
  }

  return bookings;
};

export function MonthlyCalendarView() {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const { 
    isQuickAddModalOpen, 
    selectedDate, 
    openQuickAddModal, 
    closeQuickAddModal,
    isEditModalOpen,
    selectedBooking,
    openEditModal,
    closeEditModal 
  } = useModalStore();
  const [bookingData, setBookingData] = useState<Record<string, BookingEvent[]>>({});

  // ì„ì‹œ ë”ë¯¸ ë°ì´í„° ì‚¬ìš©
  const bookings = useMemo(() => ({ ...generateDummyBookings(currentMonth), ...bookingData }), [currentMonth, bookingData]);

  // ì›”ê°„ í•©ê³„ ê³„ì‚°
  const monthlySummary = useMemo((): MonthlySummary => {
    let teamCount = 0;
    let paxCount = 0;
    let revenue = 0;
    let cost = 0;

    Object.values(bookings).forEach(dayBookings => {
      dayBookings.forEach(booking => {
        if (booking.status !== 'CANCELLED') {
          teamCount++;
          paxCount += booking.paxCount || 0;
          revenue += booking.revenue || 0;
          cost += booking.cost || 0;
        }
      });
    });

    return {
      teamCount,
      paxCount,
      revenue,
      profit: revenue - cost
    };
  }, [bookings]);

  const handleAddBooking = (date: Date) => {
    openQuickAddModal(date);
  };

  const handleQuickAddSubmit = async (data: QuickBookingFormData) => {
    // ìƒˆ ì˜ˆì•½ ë°ì´í„° ì¶”ê°€
    const newBooking: BookingEvent = {
      id: `booking-${Date.now()}`,
      typeCode: data.teamType as 'GF' | 'IN' | 'HM' | 'AT' | undefined,
      name: data.teamName,
      status: BookingStatus.PENDING,
      manager: 'ê¹€ë¯¼ìˆ˜', // ì„ì‹œ ë‹´ë‹¹ì
      paxCount: data.pax,
      date: data.departureDate,
      revenue: data.pax * 500000, // ì„ì‹œ ê³„ì‚°
      cost: data.pax * 350000 // ì„ì‹œ ê³„ì‚°
    };

    setBookingData(prev => ({
      ...prev,
      [data.departureDate]: [...(prev[data.departureDate] || []), newBooking]
    }));

    closeQuickAddModal();
    alert('ì˜ˆì•½ì´ ì¶”ê°€ë˜ì—ˆìŠµë‹ˆë‹¤!');
  };

  const handleBookingClick = (booking: BookingEvent) => {
    openEditModal(booking);
  };

  const handleEditSubmit = async (data: QuickBookingFormData) => {

    // ê¸°ì¡´ ì˜ˆì•½ ì—…ë°ì´íŠ¸
    if (selectedBooking) {
      const updatedBooking: BookingEvent = {
        ...selectedBooking,
        typeCode: data.teamType as 'GF' | 'IN' | 'HM' | 'AT' | undefined,
        name: data.teamName,
        paxCount: data.pax,
        date: data.departureDate,
        revenue: data.pax * 500000, // ì„ì‹œ ê³„ì‚°
        cost: data.pax * 350000 // ì„ì‹œ ê³„ì‚°
      };

      setBookingData(prev => {
        const newData = { ...prev };
        // ê¸°ì¡´ ë‚ ì§œì—ì„œ ì˜ˆì•½ ì œê±°
        if (newData[selectedBooking.date]) {
          newData[selectedBooking.date] = newData[selectedBooking.date].filter(b => b.id !== selectedBooking.id);
        }
        // ìƒˆ ë‚ ì§œì— ì˜ˆì•½ ì¶”ê°€
        if (!newData[data.departureDate]) {
          newData[data.departureDate] = [];
        }
        newData[data.departureDate].push(updatedBooking);
        return newData;
      });

      closeEditModal();
      alert('ì˜ˆì•½ì´ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤!');
    }
  };

  // Removed unused handleMonthChange - setCurrentMonth used directly

  return (
    <>
      <div className="h-full flex flex-col">
        {/* ìº˜ë¦°ë” ë³¸ì²´ */}
        <div className="flex-1 overflow-hidden">
          <CalendarMonth
            month={currentMonth}
            bookings={bookings}
            onAddBooking={handleAddBooking}
            onBookingClick={handleBookingClick}
            onMonthChange={setCurrentMonth}
          />
        </div>

        {/* ì›”ê°„ í•©ê³„ í‘¸í„° */}
        <MonthlySummaryFooter summary={monthlySummary} />
      </div>

      {/* ë¹ ë¥¸ ì˜ˆì•½ ì¶”ê°€ ëª¨ë‹¬ */}
      <QuickBookingModal
        isOpen={isQuickAddModalOpen}
        onClose={closeQuickAddModal}
        onSave={handleQuickAddSubmit}
