interface WeekViewMobileProps {
  currentDate: Date;
}

export default function WeekViewMobile({ currentDate }: WeekViewMobileProps) {
  const { bookings, mutate } = useBookings();
  const [isDragging, setIsDragging] = useState(false);
  const [isMounted, setIsMounted] = useState(false);
  const [currentWeek, setCurrentWeek] = useState(currentDate);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    setIsMounted(true);
  }, []);

  // ìŠ¤ì™€ì´í”„ í•¸ë“¤ëŸ¬
  const handlers = useSwipeable({
    onSwipedLeft: () => {
      if (!isTransitioning) {
        setIsTransitioning(true);
        setCurrentWeek(prev => addWeeks(prev, 1));
        setTimeout(() => setIsTransitioning(false), 300);
      }
    },
    onSwipedRight: () => {
      if (!isTransitioning) {
        setIsTransitioning(true);
        setCurrentWeek(prev => subWeeks(prev, 1));
        setTimeout(() => setIsTransitioning(false), 300);
      }
    },
    preventScrollOnSwipe: true,
    trackMouse: false,
    trackTouch: true,
    delta: 50, // ìµœì†Œ ìŠ¤ì™€ì´í”„ ê±°ë¦¬
  });

  // ì£¼ê°„ ì‹œì‘ì¼ ê³„ì‚°
  const weekStart = startOfWeek(currentWeek, { weekStartsOn: 1 }); // ì›”ìš”ì¼ ì‹œì‘
  const weekDays = Array.from({ length: 7 }, (_, i) => addDays(weekStart, i));

  // ë‚ ì§œë³„ë¡œ ì˜ˆì•½ ê·¸ë£¹í™”
  const bookingsByDate = weekDays.reduce((acc, date) => {
    const dateStr = format(date, 'yyyy-MM-dd');
    acc[dateStr] = bookings.filter(booking => 
      booking.startDate === dateStr
    );
    return acc;
  }, {} as Record<string, any[]>);

  const handleDragEnd = async (result: any) => {
    setIsDragging(false);

    if (!result.destination) return;

    const bookingId = result.draggableId;
    const newDate = result.destination.droppableId;

    const booking = bookings.find(b => b.id === bookingId);
    if (!booking) return;

    // ë‚ ì§œê°€ ë³€ê²½ëœ ê²½ìš°ì—ë§Œ ì—…ë°ì´íŠ¸
    if (booking.startDate !== newDate) {
      try {
        // Optimistic update
        await mutate(
          async (currentData: any) => {
            return currentData?.map((b: any) =>
              b.id === bookingId ? { ...b, startDate: newDate } : b
            );
          },
          { revalidate: false }
        );

        // API í˜¸ì¶œ
        await updateBooking(bookingId, { startDate: newDate });

        // ì„œë²„ ë°ì´í„°ë¡œ ê°±ì‹ 
        await mutate();

        logger.info('[Mobile] Booking moved', `${bookingId} to ${newDate} - PATCH 200`);
      } catch (error) {
        logger.error('Failed to update booking', error instanceof Error ? error.message : String(error));
        // ì—ëŸ¬ ì‹œ ë¡¤ë°±
        await mutate();
      }
    }
  };

  if (!isMounted) {
    return <div className="h-full flex items-center justify-center">Loading...</div>;
  }

  return (
    <DragDropContext 
      onDragEnd={handleDragEnd} 
      onDragStart={() => setIsDragging(true)}
    >
      {/* ì£¼ê°„ í—¤ë” */}
      <div className="bg-white border-b border-gray-200 p-3 flex items-center justify-between">
        <button
          onClick={() => setCurrentWeek(prev => subWeeks(prev, 1))}
          className="p-2 hover:bg-gray-100 rounded"
        >
          â€¹
        </button>
        <h3 className="font-medium">
          {format(weekStart, 'yyyyë…„ Mì›”')} {format(weekStart, 'dì¼')} - {format(addDays(weekStart, 6), 'dì¼')}
        </h3>
        <button
          onClick={() => setCurrentWeek(prev => addWeeks(prev, 1))}
          className="p-2 hover:bg-gray-100 rounded"
        >
          â€º
        </button>
      </div>

      {/* ëª¨ë°”ì¼ ìµœì í™”: ìŠ¤í¬ë¡¤ ê°€ëŠ¥í•œ ê°€ë¡œ ë ˆì´ì•„ì›ƒ */}
      <div 
        {...handlers} 
        className={`flex overflow-x-auto snap-x snap-mandatory h-full transition-all duration-300 ${
          isTransitioning ? 'opacity-50' : ''
        }`}
      >
        {weekDays.map((date) => {
          const dateStr = format(date, 'yyyy-MM-dd');
          const isToday = format(new Date(), 'yyyy-MM-dd') === dateStr;

          return (
            <div 
              key={dateStr} 
              className="flex-shrink-0 w-full sm:w-80 snap-center px-2"
            >
              <div className={`border rounded-lg h-full ${isToday ? 'border-blue-500' : 'border-gray-200'}`}>
                <div className={`p-3 text-center font-medium sticky top-0 z-10 ${isToday ? 'bg-blue-50' : 'bg-gray-50'}`}>
                  <div className="text-sm text-gray-500">
                    {format(date, 'EEE', { locale: ko })}
                  </div>
                  <div className={`text-lg ${isToday ? 'text-blue-600' : ''}`}>
                    {format(date, 'Mì›” dì¼')}
                  </div>
                </div>

                <Droppable droppableId={dateStr}>
                  {(provided: any, snapshot: any) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className={`p-3 min-h-[400px] ${
                        snapshot.isDraggingOver ? 'bg-blue-50' : ''
                      }`}
                      style={{
                        touchAction: isDragging ? 'none' : 'auto', // ë“œë˜ê·¸ ì¤‘ ìŠ¤í¬ë¡¤ ë°©ì§€
                      }}
                    >
                      {bookingsByDate[dateStr]?.map((booking, index) => (
                        <Draggable
                          key={booking.id}
                          draggableId={booking.id}
                          index={index}
                        >
                          {(provided: any, snapshot: any) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className={`mb-3 p-3 bg-white border rounded-lg shadow-sm ${
                                snapshot.isDragging ? 'shadow-lg opacity-90' : ''
                              }`}
                              style={{
                                ...provided.draggableProps.style,
                                touchAction: 'manipulation', // í„°ì¹˜ ìµœì í™”
                              }}
                            >
                              <div className="flex items-center justify-between mb-1">
                                <div className="font-medium truncate text-sm">
                                  {booking.customerName}
                                </div>
                                <div className="flex-shrink-0 ml-2">
                                  <StatusTag status={booking.status} size="sm" />
                                </div>
                              </div>
                              <div className="text-xs text-gray-500">
                                {booking.destination} Â· {booking.numberOfPeople}ëª…
                              </div>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </div>
            </div>
          );
        })}
      </div>
    </DragDropContext>
  );
}

import { useEffect } from 'react';
import useSWR from 'swr';
import axiosInstance from '../lib/axios';
import type { Booking } from '@entrip/shared';
import { initializeSocket, subscribeToBookingEvents, unsubscribeFromBookingEvents } from '../lib/socket';

// Fetcher function for SWR
const fetcher = (url: string) => axiosInstance.get(url).then(res => res.data);

export function useBookings() {
  const { data, error, isLoading, mutate } = useSWR<Booking[]>('/api/bookings', fetcher, {
    revalidateOnFocus: false,
    revalidateOnReconnect: false,
  });

  useEffect(() => {
    // WebSocket ì´ˆê¸°í™”
    const socket = initializeSocket();

    if (socket) {
      // WebSocket ì´ë²¤íŠ¸ êµ¬ë…
      subscribeToBookingEvents({
        onCreate: (_event) => {
          // console.log('[useBookings] booking:create event received', event.bookingId);
          mutate(); // SWR ìºì‹œ ê°±ì‹ 
        },
        onUpdate: (_event) => {
          // console.log('[useBookings] booking:update event received', event.bookingId);
          mutate(); // SWR ìºì‹œ ê°±ì‹ 
        },
        onDelete: (_event) => {
          // console.log('[useBookings] booking:delete event received', event.bookingId);
          mutate(); // SWR ìºì‹œ ê°±ì‹ 
        },
        onBulkCreate: (_event) => {
          // console.log('[useBookings] booking:bulk-create event received', event.count);
          mutate(); // SWR ìºì‹œ ê°±ì‹ 
        },
        onBulkDelete: (_event) => {
          // console.log('[useBookings] booking:bulk-delete event received', event.count);
          mutate(); // SWR ìºì‹œ ê°±ì‹ 
        }
      });
    }

    // Cleanup
    return () => {
      unsubscribeFromBookingEvents();
    };
  }, []); // ë¹ˆ ì˜ì¡´ì„± ë°°ì—´ - ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸ ì‹œ í•œ ë²ˆë§Œ ì‹¤í–‰

  return {
    bookings: data || [],
    isLoading,
    isError: error,
    mutate,
  };
}

// Hook for single booking
export function useBooking(id: string | null) {
  const { data, error, isLoading, mutate } = useSWR<Booking>(
    id ? `/api/bookings/${id}` : null,
    fetcher
  );

  return {
    booking: data,
    isLoading,
    isError: error,
    mutate,
  };
}

// Mutation functions
export async function createBooking(booking: Partial<Booking>) {
  const response = await axiosInstance.post('/api/bookings', booking);
  return response.data;
}

export async function updateBooking(id: string, booking: Partial<Booking>) {
  const response = await axiosInstance.put(`/api/bookings/${id}`, booking);
  return response.data;
}

export async function deleteBooking(id: string) {
  const response = await axiosInstance.delete(`/api/bookings/${id}`);
  return response.data;
}

import React, { useState, useRef, useCallback } from 'react';
import { toast } from 'react-toastify';
import axiosInstance from '../lib/axios';

interface UndoState {
  type: 'delete';
  data: any[];
  timestamp: number;
}

export const useBulkUndo = () => {
  const [undoStack, setUndoStack] = useState<UndoState[]>([]);
  const undoTimerRef = useRef<NodeJS.Timeout | null>(null);
  const toastIdRef = useRef<string | number | null>(null);

  const addUndoItem = useCallback((type: 'delete', data: any[]) => {
    const undoItem: UndoState = {
      type,
      data,
      timestamp: Date.now()
    };

    setUndoStack(prev => [...prev, undoItem]);

    // ê¸°ì¡´ íƒ€ì´ë¨¸ ì·¨ì†Œ
    if (undoTimerRef.current) {
      clearTimeout(undoTimerRef.current);
    }

    // Toast í‘œì‹œ
    toastIdRef.current = toast.info(
      React.createElement('div', { className: "flex items-center justify-between" },
        React.createElement('span', {}, `${data.length}ê°œ í•­ëª©ì´ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤`),
        React.createElement('button', {
          onClick: () => handleUndo(undoItem),
          className: "ml-4 px-3 py-1 bg-white text-blue-600 rounded hover:bg-blue-50"
        }, 'ì‹¤í–‰ ì·¨ì†Œ')
      ),
      {
        autoClose: 5000,
        closeButton: false,
        onClose: () => {
          // Toastê°€ ë‹«íˆë©´ undo stackì—ì„œ ì œê±°
          setUndoStack(prev => prev.filter(item => item !== undoItem));
        }
      }
    );

    // 5ì´ˆ í›„ ìë™ìœ¼ë¡œ undo stackì—ì„œ ì œê±°
    undoTimerRef.current = setTimeout(() => {
      setUndoStack(prev => prev.filter(item => item !== undoItem));
      toastIdRef.current = null;
    }, 5000);
  }, []);

  const handleUndo = useCallback(async (undoItem: UndoState) => {
    try {
      if (undoItem.type === 'delete') {
        // ì‚­ì œëœ í•­ëª©ë“¤ì„ ë³µì›
        const response = await axiosInstance.post('/api/bookings/bulk-restore', {
          bookings: undoItem.data
        });

        if (response.status === 200) {
          toast.success(`${undoItem.data.length}ê°œ í•­ëª©ì´ ë³µì›ë˜ì—ˆìŠµë‹ˆë‹¤`);

          // Toast ë‹«ê¸°
          if (toastIdRef.current) {
            toast.dismiss(toastIdRef.current);
          }

          // Undo stackì—ì„œ ì œê±°
          setUndoStack(prev => prev.filter(item => item !== undoItem));

          // íƒ€ì´ë¨¸ ì •ë¦¬
          if (undoTimerRef.current) {
            clearTimeout(undoTimerRef.current);
            undoTimerRef.current = null;
          }

          return true;
        }
      }
    } catch (error) {
      console.error('Undo ì‹¤íŒ¨:', error);
      toast.error('ë³µì›ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤');
    }

    return false;
  }, []);

  const clearUndo = useCallback(() => {
    setUndoStack([]);
    if (undoTimerRef.current) {
      clearTimeout(undoTimerRef.current);
      undoTimerRef.current = null;
    }
    if (toastIdRef.current) {
      toast.dismiss(toastIdRef.current);
      toastIdRef.current = null;
    }
  }, []);

  return {
    addUndoItem,
    handleUndo,
    clearUndo,
    hasUndo: undoStack.length > 0
  };
};

import { io, Socket } from 'socket.io-client';
import Cookies from 'js-cookie';
import { logger } from '@entrip/shared';

let socket: Socket | null = null;

export const initializeSocket = (): Socket | undefined => {
  const token = Cookies.get('auth-token');

  if (!token) {
    logger.error('[Socket]', 'No auth token found');
    return;
  }

  if (socket?.connected) {
    logger.info('[Socket]', 'Already connected');
    return;
  }

  socket = io(process.env.NEXT_PUBLIC_API_URL || 'http://localhost:4000', {
    auth: {
      token
    },
    path: '/socket.io/',
    transports: ['websocket', 'polling'],
  });

  socket.on('connect', () => {
    logger.info('[Socket]', 'Connected to server');
  });

  socket.on('disconnect', () => {
    logger.info('[Socket]', 'Disconnected from server');
  });

  socket.on('connect_error', (error) => {
    logger.error('[Socket]', `Connection error: ${error.message}`);
  });

  return socket;
};

export const disconnectSocket = () => {
  if (socket) {
    socket.disconnect();
    socket = null;
  }
};

export const getSocket = (): Socket | null => socket;

// ì˜ˆì•½ ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ë“±ë¡
export const subscribeToBookingEvents = (callbacks: {
  onCreate?: (data: any) => void;
  onUpdate?: (data: any) => void;
  onDelete?: (data: any) => void;
  onBulkCreate?: (data: any) => void;
  onBulkDelete?: (data: any) => void;
}) => {
  if (!socket) {
    logger.error('[Socket]', 'Socket not initialized');
    return;
  }

  if (callbacks.onCreate) {
    socket.on('booking:create', (data) => {
      logger.info('[Socket]', `Received booking:create ${JSON.stringify(data)}`);
      callbacks.onCreate!(data);
    });
  }

  if (callbacks.onUpdate) {
    socket.on('booking:update', (data) => {
      logger.info('[Socket]', `Received booking:update ${JSON.stringify(data)}`);
      callbacks.onUpdate!(data);
    });
  }

  if (callbacks.onDelete) {
    socket.on('booking:delete', (data) => {
      logger.info('[Socket]', `Received booking:delete ${JSON.stringify(data)}`);
      callbacks.onDelete!(data);
    });
  }

  if (callbacks.onBulkCreate) {
    socket.on('booking:bulk-create', (data) => {
      logger.info('[Socket]', `Received booking:bulk-create ${JSON.stringify(data)}`);
      callbacks.onBulkCreate!(data);
    });
  }

  if (callbacks.onBulkDelete) {
    socket.on('booking:bulk-delete', (data) => {
      logger.info('[Socket]', `Received booking:bulk-delete ${JSON.stringify(data)}`);
      callbacks.onBulkDelete!(data);
    });
  }
};

// ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ í•´ì œ
export const unsubscribeFromBookingEvents = () => {
  if (!socket) return;

  socket.off('booking:create');
  socket.off('booking:update');
  socket.off('booking:delete');
  socket.off('booking:bulk-create');
  socket.off('booking:bulk-delete');
};

// í•­ê³µí¸ ì§€ì—° ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ë“±ë¡
export const subscribeToFlightDelays = (onDelay: (data: any) => void) => {
  if (!socket) {
    logger.error('[Socket]', 'Socket not initialized');
    return;
  }

  socket.on('delay', (data) => {
    logger.info('[Socket]', `Received flight delay: ${JSON.stringify(data)}`);
    onDelay(data);
  });
};

// í•­ê³µí¸ ê°ì‹œ ì‹œì‘/ì¤‘ì§€
export const watchFlight = (flightNo: string) => {
  if (!socket) {
    logger.error('[Socket]', 'Socket not initialized');
    return;
  }

  socket.emit('watch:flight', flightNo);
  logger.info('[Socket]', `Watching flight ${flightNo}`);
};

export const unwatchFlight = (flightNo: string) => {
  if (!socket) {
    logger.error('[Socket]', 'Socket not initialized');
    return;
  }

  socket.emit('unwatch:flight', flightNo);
  logger.info('[Socket]', `Unwatching flight ${flightNo}`);
};

// í•­ê³µí¸ ì§€ì—° ë¦¬ìŠ¤ë„ˆ í•´ì œ
export const unsubscribeFromFlightDelays = () => {
  if (!socket) return;
  socket.off('delay');
};

