  // ì›Œí¬ë¶ ìƒì„±
  const wb = XLSX.utils.book_new();
  const ws_data: any[][] = [];

  // íšŒì‚¬ í—¤ë” (ë³‘í•© ì…€)
  ws_data.push(['ENTRIP ì˜ˆì•½ ëª©ë¡']);
  ws_data.push([`ìƒì„±ì¼: ${format(new Date(), 'yyyyë…„ MMì›” ddì¼', { locale: ko })}`]);
  ws_data.push([]); // ë¹ˆ ì¤„

  // í…Œì´ë¸” í—¤ë”
  const headers = ['ë²ˆí˜¸', 'ì˜ˆì•½ë²ˆí˜¸', 'ê³ ê°ëª…', 'íŒ€ëª…', 'ëª©ì ì§€', 'ì¶œë°œì¼', 'ë„ì°©ì¼', 'ì¸ì›', 'ìƒíƒœ', 'ë§¤ì¶œ', 'ë‹´ë‹¹ì'];
  ws_data.push(headers);

  // ë°ì´í„° í–‰
  bookings.forEach((booking, index) => {
    ws_data.push([
      index + 1,
      booking.bookingNumber || `B2025-${String(index + 1).padStart(3, '0')}`,
      booking.customerName,
      booking.teamName || booking.customerName,
