  });

  // ìš”ì•½ ì •ë³´
  ws_data.push([]); // ë¹ˆ ì¤„
  ws_data.push(['ì´ ì˜ˆì•½ ê±´ìˆ˜:', bookings.length + 'ê±´']);
  ws_data.push(['ì´ ì¸ì›:', bookings.reduce((sum, b) => sum + b.numberOfPeople, 0) + 'ëª…']);
  ws_data.push(['ì´ ë§¤ì¶œ:', bookings.reduce((sum, b) => sum + (b.revenue || 0), 0).toLocaleString() + 'ì›']);

  // ì›Œí¬ì‹œíŠ¸ ìƒì„±
  const ws = XLSX.utils.aoa_to_sheet(ws_data);

  // ë³‘í•© ì…€ ì„¤ì •
  ws['!merges'] = [
    { s: { r: 0, c: 0 }, e: { r: 0, c: 10 } }, // ì œëª© í–‰
    { s: { r: 1, c: 0 }, e: { r: 1, c: 10 } }, // ë‚ ì§œ í–‰
