    { wch: 12 },  // ì˜ˆì•½ë²ˆí˜¸
    { wch: 15 },  // ê³ ê°ëª…
    { wch: 15 },  // íŒ€ëª…
    { wch: 15 },  // ëª©ì ì§€
    { wch: 12 },  // ì¶œë°œì¼
    { wch: 12 },  // ë„ì°©ì¼
    { wch: 8 },   // ì¸ì›
    { wch: 10 },  // ìƒíƒœ
    { wch: 15 },  // ë§¤ì¶œ
    { wch: 10 }   // ë‹´ë‹¹ì
  ];

  // ì›Œí¬ë¶ì— ì‹œíŠ¸ ì¶”ê°€
  XLSX.utils.book_append_sheet(wb, ws, 'ì˜ˆì•½ëª©ë¡');

  // íŒŒì¼ ë‹¤ìš´ë¡œë“œ
  const date = format(new Date(), 'yyyy-MM-dd');
